import React from "react";
import styled from "styled-components";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";
import axios from "axios";

const Table = styled.table`
    width: 100%;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
    width: 1200px;
    margin: 20px;
`;

export const Thead = styled.thead``;

export const Tbody = styled.tbody``;

export const Tr = styled.tr``;

export const Th = styled.th`
    text-align: start;
    border-bottom: inset;
    padding-bottom: 5px;
`;

export const Td = styled.td`
    padding-top: 15px;
    text-align: ${(props) => (props.alignCenter ? "center" : "start")};
    width: ${(props) => (props.width ? props.width : "auto")}; 
`;

const IconButton = styled.div`
  cursor: pointer;

  &:hover {
    color: #007bff; /* Mudando a cor do ícone ao passar o mouse (ajuste a cor conforme preferir) */
  }
`;

const Grid = ({ users, setUsers, setOnEdit }) => {

    const hidePassword = (senha) => {
        return '*'.repeat(senha.length); // Substitui a senha por asteriscos
    };

    const handleEdit = (item) => {
        setOnEdit(item);
    };

    const handleDelete = async (id) => {
        const confirmed = window.confirm("Deseja confirmar a exclusão?");

        if (confirmed) {
            await axios
                .delete("http://localhost:8800/" + id)
                .then(({ data }) => {
                    const newArray = users.filter((user) => user.id !== id);
                    setUsers(newArray);
                    toast.success(data);
                })
                .catch(({ data }) => toast.error(data));
            setOnEdit(null);
        } else {
            toast.info("Exclusão cancelada.");
        }
    };

    return(
        <Table>
            <Thead>
                <Tr>
                    <Th>Nome</Th>
                    <Th>Email</Th>
                    <Th>Senha</Th>
                    <Th>Telefone</Th>
                    <Th>Data Nascimento</Th>
                    <Th></Th>
                    <Th></Th>
                </Tr>
            </Thead>
            <Tbody>
                {users.map((item, i) => (
                    <Tr key={i}>
                        <Td width="25%">{item.nome}</Td>
                        <Td width="25%">{item.email}</Td>
                        <Td width="15%">{hidePassword(item.senha)}</Td>
                        <Td width="15%">{item.telefone}</Td>
                        <Td width="15%">{item.data_nasc}</Td>
                        <Td alignCenter width="2.5%">
                            <IconButton onClick={() => handleEdit(item)}>
                                <FaEdit />
                            </IconButton>
                        </Td>
                        <Td alignCenter width="2.5%">
                            <IconButton onClick={() => handleDelete(item.id)}>
                                <FaTrash />
                            </IconButton>
                        </Td>
                    </Tr>
                ))}
            </Tbody>
        </Table>
    );
};

export default Grid;
