import GlobalStyle from "./styles/global";
import styled from "styled-components";
import Form from "./form.js";
import Grid from "./grid.js";
import { useEffect, useState } from "react";
import { toast, ToastContainer } from "react-toastify"; //FeedBack em tela quando usuário faz a ação.
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";

const Container = styled.div`
    width: 100%;
    max-width: 900px;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Title = styled.h2``;

function App(){  // Definindo componente funcional
    const [users, setUsers] = useState([]);
    const [onEdit, setOnEdit] = useState(null);

    const getUsers = async () => {  // Assíncrona pois precisa esperar o banco de dados retornar os dados
        try{
            const res = await axios.get("http://localhost:8800");
            setUsers(res.data);
        } catch(error){
            toast.error(error);
        }
    };

    useEffect(() => {
        getUsers();
    }, [setUsers]);

    return (
        <>
            <Container>
                <Title>DASHBOARD DE CONTROLE DE USUÁRIOS</Title>
                <Form />
                <Grid users={users} />
            </Container>
            <ToastContainer autoClose={3000} position="bottom-left" />
            <GlobalStyle /> 
        </>
    );
}

export default App;
