import React, { useRef } from "react";
import styled from "styled-components";


const FormContainer = styled.form`
    display: flex;
    align-items: flex-end;
    gap: 10px;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
`;

const InputArea = styled.div`
    display: flex;
    flex-direction: column;
`;

const Input = styled.div`
    width: 145px;
    padding: 0 10px;
    border: 1px solid #bbb;
    height: 40px;
    cursor: text;
`;

const Button = styled.button`
    padding: 10px;
    cursor: pointer;
    border-radius: 5px;
    border: none;
    background-color: #2c73d2;
    color: white;
    height: 42px;
`;

const Title = styled.h3`
    font-size: 20px;
    font-weight: bold;
    margin-bottom: 20px;
    color: #333;
`;

const Label = styled.label``;

const Form = ({ onEdit }) => {
    const ref = useRef(); // Armazena valores mutáveis sem causar uma nova renderização quando os valores mudam.
    return (
        <>
            {/* Container para o Título */}
            <TitleContainer>
                <Title>Criação de Novo Usuário</Title>
            </TitleContainer>

            {/* Formulário com Inputs */}
            <FormContainer ref={ref}>
                <InputArea>
                    <Label>Nome</Label>
                    <Input name="Nome" />
                </InputArea>

                <InputArea>
                    <Label>E-mail</Label>
                    <Input name="email" type="email" />
                </InputArea>

                <InputArea>
                    <Label>Senha</Label>
                    <Input name="senha" type="password" />
                </InputArea>

                <InputArea>
                    <Label>Telefone</Label>
                    <Input name="telefone" />
                </InputArea>

                <InputArea>
                    <Label>Data de Nascimento</Label>
                    <Input name="data_nasc" type="date" />
                </InputArea>

                <Button type="submit">SALVAR</Button>
            </FormContainer>
        </>
    );
}

export default Form;
